import React from 'react';
import Form from './components/Form';
import Card from './components/Card';
import './App.css';

class App extends React.Component {
  constructor() {
    super();

    this.state = {
      cardName: '',
      cardDescription: '',
      cardImage: '',
      cardAttr1: 0,
      cardAttr2: 0,
      cardAttr3: 0,
      cardRare: 'normal',
      cardTrunfo: false,
      hasTrunfo: false,
      isSaveButtonDisabled: true,
      cards: [],
    };
  }

  validationButton = () => {
    const {
      cardName,
      cardDescription,
      cardImage,
      cardAttr1,
      cardAttr2,
      cardAttr3,
    } = this.state;

    const limitAttr = 90;
    const attr1Validation = cardAttr1 >= 0 && cardAttr1 <= limitAttr;
    const attr2Validation = cardAttr2 >= 0 && cardAttr2 <= limitAttr;
    const attr3Validation = cardAttr3 >= 0 && cardAttr3 <= limitAttr;

    const totalAttr = Number(cardAttr1) + Number(cardAttr2) + Number(cardAttr3);
    const attrsLimit = 210;
    const validationAllAttr = totalAttr <= attrsLimit;

    const errorCases = [
      !cardName.length,
      !cardDescription.length,
      !attr1Validation,
      !attr2Validation,
      !attr3Validation,
      !validationAllAttr,
      !cardImage.length,
    ];

    const filledForm = errorCases.every((error) => error !== true);
    this.setState({ isSaveButtonDisabled: !filledForm });
  }

  hasTrunfo = (checkbox) => {
    console.log(checkbox)
    if (checkbox) {
      this.setState({
        hasTrunfo: checkbox,
      }, () => console.log(this.state.hasTrunfo));
    }
  };

  inputChange = ({ target }) => {
    const { id } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    this.setState({
      [id]: value,
    }, this.validationButton);
    if (target.type === 'checkbox') {
      this.setState({
        hasTrunfo: target.checked,
      }, this.hasTrunfo);
    }
  }

  onSaveButtonClick = (event) => {
    event.preventDefault();

    const {
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      hasTrunfo,
    } = this.state;

    this.setState((prevState) => ({
      cardName: '',
      cardDescription: '',
      cardImage: '',
      cardAttr1: 0,
      cardAttr2: 0,
      cardAttr3: 0,
      cardRare: 'normal',
      cardTrunfo: false,
      hasTrunfo,
      isSaveButtonDisabled: true,
      cards: [
        ...prevState.cards,
        {
          cardName,
          cardDescription,
          cardAttr1,
          cardAttr2,
          cardAttr3,
        }],
    }), this.hasTrunfo(hasTrunfo));
  }

  render() {
    const {
      cardName,
      cardDescription,
      cardImage,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardRare,
      cardTrunfo,
      hasTrunfo,
      isSaveButtonDisabled,
    } = this.state;

    return (
      <div>
        <Form
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardImage={ cardImage }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardRare={ cardRare }
          cardTrunfo={ cardTrunfo }
          hasTrunfo={ hasTrunfo }
          isSaveButtonDisabled={ isSaveButtonDisabled }
          onInputChange={ this.inputChange }
          onSaveButtonClick={ this.onSaveButtonClick }
        />

        <Card
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardImage={ cardImage }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardRare={ cardRare }
          cardTrunfo={ cardTrunfo }
        />
      </div>
    );
  }
}

export default App;
