import React, { Component } from 'react';
import PropTypes from 'prop-types';
import Input from './Input';
import InputSelect from './InputSelect';
import TextArea from './TextArea';

export default class Form extends Component {
  render() {
    const {
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardTrunfo,
      isSaveButtonDisabled,
      onInputChange,
      onSaveButtonClick } = this.props;

    const options = ['normal', 'raro', 'muito raro'];

    return (
      <form className="formCard">
        <Input
          type="text"
          label="Nome da Carta"
          id="InputName"
          dataTest="name-input"
          value={ cardName }
          onChange={ onInputChange }
        />

        <TextArea
          label="Descrição da carta"
          id="descTextArea"
          dataTest="description-input"
          value={ cardDescription }
          onChange={ onInputChange }
        />

        <Input
          type="number"
          label="Atributo 1"
          id="descTextArea"
          dataTest="attr1-input"
          value={ cardAttr1 }
          onChange={ onInputChange }
        />

        <Input
          type="number"
          label="Atributo 2"
          id="descTextArea"
          dataTest="attr2-input"
          value={ cardAttr2 }
          onChange={ onInputChange }
        />

        <Input
          type="number"
          label="Atributo 3"
          id="descTextArea"
          dataTest="attr3-input"
          value={ cardAttr3 }
          onChange={ onInputChange }
        />

        <Input
          type="text"
          label="Imagem"
          id="InputName"
          dataTest="image-input"
          value={ cardImage }
          onChange={ onInputChange }
        />

        <InputSelect
          label="Raridade"
          options={ options }
          id="inputSelect"
          dataTest="rare-input"
          value={ cardRare }
          onChange={ onInputChange }
        />

        <Input
          type="checkbox"
          label="É Super Trunfo?"
          id="InputName"
          dataTest="trunfo-input"
          checked={ cardTrunfo }
          onChange={ onInputChange }
        />

        <button
          type="submit"
          data-testid="save-button"
          disabled={ isSaveButtonDisabled }
          onClick={ onSaveButtonClick }
        >
          Salvar
        </button>
      </form>
    );
  }
}

Form.propTypes = {
  cardName: PropTypes.string,
  cardDescription: PropTypes.string,
  cardAttr1: PropTypes.number,
  cardAttr2: PropTypes.number,
  cardAttr3: PropTypes.number,
  cardImage: PropTypes.string,
  cardRare: PropTypes.string,
  cardTrunfo: PropTypes.bool,
  hasTrunfo: PropTypes.string,
  isSaveButtonDisabled: PropTypes.bool,
  onInputChange: PropTypes.func,
  onSaveButtonClick: PropTypes.func,
}.isRequired;
