import React from 'react';
import Form from './components/Form';
import Card from './components/Card';
import './App.css';

class App extends React.Component {
  constructor() {
    super();

    this.state = {
      cardName: 'foio',
    };
  }

  inputChange = ({ target }) => {
    const { name, value } = target;
    this.setState({
      [name]: value,
    });
  }

  render() {
    const { cardName } = this.state;

    return (
      <div>
        <Form cardName={ cardName } onInputChange={ this.inputChange } />
        <Card />
      </div>
    );
  }
}

export default App;
