import React from 'react';
import Form from './components/Form';
import Card from './components/Card';
import CardSaves from './components/CardSaves';
import './App.css';
import Input from './components/Input';
import InputSelect from './components/InputSelect';
import InputCheckbox from './components/InputCheckbox';

class App extends React.Component {
  constructor() {
    super();

    this.state = {
      cardName: '',
      cardDescription: '',
      cardImage: '',
      cardAttr1: '0',
      cardAttr2: '0',
      cardAttr3: '0',
      cardRare: 'normal',
      cardTrunfo: false,
      hasTrunfo: false,
      isSaveButtonDisabled: true,
      cardsSaves: [],
      nameFind: '',
      rareFind: 'todas',
      superTrunfoFind: false,
    };
  }

  validationButton = () => {
    const {
      cardName,
      cardDescription,
      cardImage,
      cardAttr1,
      cardAttr2,
      cardAttr3,
    } = this.state;

    const limitAttr = 90;
    const attr1Validation = cardAttr1 >= 0 && cardAttr1 <= limitAttr;
    const attr2Validation = cardAttr2 >= 0 && cardAttr2 <= limitAttr;
    const attr3Validation = cardAttr3 >= 0 && cardAttr3 <= limitAttr;

    const totalAttr = Number(cardAttr1) + Number(cardAttr2) + Number(cardAttr3);
    const attrsLimit = 210;
    const validationAllAttr = totalAttr <= attrsLimit;

    const errorCases = [
      !cardName.length,
      !cardDescription.length,
      !attr1Validation,
      !attr2Validation,
      !attr3Validation,
      !validationAllAttr,
      !cardImage.length,
    ];

    const filledForm = errorCases.every((error) => error !== true);
    this.setState({ isSaveButtonDisabled: !filledForm });
  }

  inputChange = ({ target }) => {
    const { id } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    this.setState({
      [id]: value,
    }, this.validationButton);
  }

  hasTrunfo = (trunfo) => {
    this.setState({
      hasTrunfo: trunfo,
    });
  }

  onSaveButtonClick = (event) => {
    event.preventDefault();

    const {
      cardName,
      cardImage,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardRare,
      cardTrunfo,
    } = this.state;

    if (cardTrunfo) this.hasTrunfo(cardTrunfo);

    this.setState((prevState) => ({
      cardName: '',
      cardDescription: '',
      cardImage: '',
      cardAttr1: '0',
      cardAttr2: '0',
      cardAttr3: '0',
      cardRare: 'normal',
      cardTrunfo: false,
      isSaveButtonDisabled: true,
      cardsSaves: [
        ...prevState.cardsSaves,
        {
          cardName,
          cardImage,
          cardDescription,
          cardAttr1,
          cardAttr2,
          cardAttr3,
          cardRare,
          cardTrunfo,
        }],
    }));
  }

  removeButton = ({ target }) => {
    const { cardsSaves } = this.state;
    const { name } = target;

    const hasTrunfo = cardsSaves.find((card) => card.cardName === name).cardTrunfo;

    const newArray = cardsSaves.filter((card) => card.cardName !== name);
    this.setState({
      cardsSaves: newArray,
    });

    if (hasTrunfo) {
      this.setState({
        hasTrunfo: false,
      });
    }
  }

  findInput = ({ target }) => {
    const { id } = target;
    const value = target.type === 'checkbox' ? target.checked : target.value;
    this.setState({
      [id]: value,
    });
  }

  render() {
    const {
      cardName,
      cardDescription,
      cardImage,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardRare,
      cardTrunfo,
      hasTrunfo,
      isSaveButtonDisabled,
      cardsSaves,
      nameFind,
      rareFind,
      superTrunfoFind,
    } = this.state;

    let findName = nameFind.length
      ? cardsSaves.filter((card) => card.cardName.includes(nameFind))
      : cardsSaves;

    findName = rareFind !== 'todas'
      ? findName.filter((card) => card.cardRare === rareFind)
      : findName;

    const options = ['todas', 'normal', 'raro', 'muito raro'];

    return (
      <div>
        <Form
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardImage={ cardImage }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardRare={ cardRare }
          cardTrunfo={ cardTrunfo }
          hasTrunfo={ hasTrunfo }
          isSaveButtonDisabled={ isSaveButtonDisabled }
          onInputChange={ this.inputChange }
          onSaveButtonClick={ this.onSaveButtonClick }
        />

        <Card
          cardName={ cardName }
          cardDescription={ cardDescription }
          cardImage={ cardImage }
          cardAttr1={ cardAttr1 }
          cardAttr2={ cardAttr2 }
          cardAttr3={ cardAttr3 }
          cardRare={ cardRare }
          cardTrunfo={ cardTrunfo }
        />

        <InputCheckbox
          type="checkbox"
          id="superTrunfoFind"
          label="Super Trunfo"
          dataTest="trunfo-filter"
          checked={ superTrunfoFind }
          onChange={ this.findInput }
        />

        <Input
          id="nameFind"
          type="text"
          label="Nome"
          dataTest="name-filter"
          value={ nameFind }
          onChange={ this.findInput }
        />

        <InputSelect
          id="rareFind"
          type="text"
          label="Raridade"
          dataTest="rare-filter"
          options={ options }
          value={ rareFind }
          onChange={ this.findInput }
        />

        { findName.map((card) => (
          <CardSaves
            key={ card.cardName }
            cardName={ card.cardName }
            cardDescription={ card.cardDescription }
            cardImage={ card.cardImage }
            cardAttr1={ card.cardAttr1 }
            cardAttr2={ card.cardAttr2 }
            cardAttr3={ card.cardAttr3 }
            cardRare={ card.cardRare }
            cardTrunfo={ card.cardTrunfo }
            removeButton={ this.removeButton }
          />
        )) }
      </div>
    );
  }
}

export default App;
