import React, { Component } from 'react';
import PropTypes from 'prop-types';
import Input from './Input';
import InputSelect from './InputSelect';
import TextArea from './TextArea';
import InputCheckbox from './InputCheckbox';

export default class Form extends Component {
  render() {
    const {
      cardName,
      cardDescription,
      cardAttr1,
      cardAttr2,
      cardAttr3,
      cardImage,
      cardRare,
      cardTrunfo,
      hasTrunfo,
      isSaveButtonDisabled,
      onInputChange,
      onSaveButtonClick,
    } = this.props;

    const options = ['normal', 'raro', 'muito raro'];

    return (
      <form className="formCard" onSubmit={ onSaveButtonClick }>
        <Input
          type="text"
          label="Nome da Carta"
          id="cardName"
          dataTest="name-input"
          value={ cardName }
          onChange={ onInputChange }
        />

        <TextArea
          label="Descrição da carta"
          id="cardDescription"
          dataTest="description-input"
          value={ cardDescription }
          onChange={ onInputChange }
        />

        <Input
          type="number"
          label="Atributo 1"
          id="cardAttr1"
          dataTest="attr1-input"
          value={ cardAttr1 }
          onChange={ onInputChange }
        />

        <Input
          type="number"
          label="Atributo 2"
          id="cardAttr2"
          dataTest="attr2-input"
          value={ cardAttr2 }
          onChange={ onInputChange }
        />

        <Input
          type="number"
          label="Atributo 3"
          id="cardAttr3"
          dataTest="attr3-input"
          value={ cardAttr3 }
          onChange={ onInputChange }
        />

        <Input
          type="text"
          label="Imagem"
          id="cardImage"
          dataTest="image-input"
          value={ cardImage }
          onChange={ onInputChange }
        />

        <InputSelect
          label="Raridade"
          options={ options }
          id="cardRare"
          dataTest="rare-input"
          value={ cardRare }
          onChange={ onInputChange }
        />

        { hasTrunfo
          ? <span>Você já tem um Super Trunfo em seu baralho</span>
          : <InputCheckbox
            type="checkbox"
            label="Super Trunfo"
            id="cardTrunfo"
            dataTest="trunfo-input"
            checked={ cardTrunfo }
            onChange={ onInputChange }
          /> }

        <button
          type="submit"
          data-testid="save-button"
          disabled={ isSaveButtonDisabled }
        >
          Salvar
        </button>
      </form>
    );
  }
}

Form.propTypes = {
  cardName: PropTypes.string,
  cardDescription: PropTypes.string,
  cardAttr1: PropTypes.string,
  cardAttr2: PropTypes.string,
  cardAttr3: PropTypes.string,
  cardImage: PropTypes.string,
  cardRare: PropTypes.string,
  hasTrunfo: PropTypes.bool,
  isSaveButtonDisabled: PropTypes.bool,
  onInputChange: PropTypes.func,
  onSaveButtonClick: PropTypes.func,
}.isRequired;
